import { mode, Styles, StyleFunctionProps } from "@chakra-ui/theme-tools";

const styles: Styles = {
  global: (props: StyleFunctionProps) => ({
    "#root": {
      h: "100vh",
      bg: mode("white", "purple"),
    },
  }),
};

export default styles;
